import com.eviware.soapui.support.XmlHolder

def res=context.testCase.getTestStepByName('saveCustomer').getPropertyValue("Response")

log.info res

def holder=new XmlHolder(res)
def result=holder.getNodeValue("//*:id")
context.testCase.setPropertyValue("id",result)

log.info "Customer_id is "+result

con:config.script:
import jxl.*
def username,lastname,email

String filepath="C:/Users/RahulPoddar/Desktop/SDET/SampleData1.xls"
File Excel=new File(filepath)

Workbook wb=Workbook.getWorkbook(Excel)
Sheet sh=wb.getSheet("Sheet1")

def rowsize=sh.getRows();
def columnsize=sh.getColumns();

log.info rowsize
log.info columnsize

for (def i=0;i<rowsize;i++)
{
	for (def j=0;j<columnsize;j++)
	{
		if (j==0)
		{
			username=sh.getCell(j,i).getContents();
		}
		if (j==1)
		{
			lastname=sh.getCell(j,i).getContents();
		}
		else{
			email=sh.getCell(j,i).getContents();
		}
	}
	def tc=testRunner.testCase.testSuite.project.testSuites['GetCustomer'].testCases['TC01']
	tc.testSteps['Property'].setPropertyValue("name",username)
	tc.testSteps['Property'].setPropertyValue("lastname",lastname)
	tc.testSteps['Property'].setPropertyValue("email",email)
	log.info "Username is "+username
	log.info "Lastname is "+lastname
	log.info "Email is "+email

	def runner=tc.run(null,false)
	log.info "Total no. of steps are "+runner.results.size()
}
